// src/pages/News.tsx
import { useState } from "react";
import { Link } from "react-router-dom";

// ALL LOGOS — English + Kiswahili + Gĩkũyũ (real & working links)
const NEWS_LOGOS: Record<string, string> = {
  // English
  "Tuko.co.ke": "data:image/png;base64,",
  "Citizen Digital": "data:image/png;base64,",
  "Nation Africa": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQcveoV8c10kpYeCKM7mYoYC0qHmSy6GZHZxjIVMUygtw&s",
  "The Standard": "data:image/png;base64,",
  "The Star": "https://yt3.googleusercontent.com/ytc/AIdro_mb1Ls3yj2F3sLWdOswezwTsY38KJJ82n04mgYa_oblXg=s900-c-k-c0x00ffffff-no-rj",
  "Kenyans.co.ke": "data:image/jpeg;base64,",
  "Pulse Live Kenya": "data:image/png;base64,",
  "Nairobi Leo": "data:image/png;base64,",
  "KBC": "data:image/jpeg;base64,",

  // Kiswahili
  "Tuko Kiswahili": "data:image/png;base64,", // Same brand
  "KBC Kiswahili": "data:image/jpeg;base64,",
  "Habari Now": "data:image/jpeg;base64,",

  // Gĩkũyũ Stations (Royal Media Services)
  "Coro FM": "data:image/png;base64,",
  "Inooro FM": "data:image/jpeg;base64,",
  "Kameme FM": "https://yt3.googleusercontent.com/hBfOJd_O-O8XiKLHjK2DNgiDdt7K4QCeue4rGdTHGjxwHJf9U5GW5dfc_i3ZkhVsrdduLb2j=s900-c-k-c0x00ffffff-no-rj",
};

const CATEGORY_IMAGES = {
  GVB: "https://growthcatalysts.org/wp-content/uploads/2024/03/understanding-gender-based-violence-in-kenya.png?w=1024",
  Crime: "https://images.hivisasa.com/1200/ur7MebjAoMsupreme-court-fountain-kenya.jpg",
  Politics: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSrvmddvbcv6bqqIsxi4T_2ySt_B0UxhNc84g&s",
  Business: "https://mediaengagement.org/wp-content/uploads/2016/06/shutterstock_297892517-600x374.jpg",
  Other: "https://nairobileo.co.ke/storage/uploads/2025/07/Screenshot%202025-07-24%20182843-1753371074.jpg",
};

const EXPLORE_IMAGES = {
  People: "https://saccoreview.co.ke/wp-content/uploads/2025/07/President-Ruto-og_image.webp",
  Places: "data:image/jpeg;base64,",
  Organizations: "https://procoolmfg.com/wp-content/uploads/2024/11/Top-Food-and-Beverage-Manufacturing-Companies-in-Kenya.jpg",
};

export default function News() {
  const [lang, setLang] = useState<"english" | "swahili" | "kikuyu">("english");

  const sources = {
    english: [
      "Tuko.co.ke", "Citizen Digital", "Nation Africa", "The Standard",
      "The Star", "Kenyans.co.ke", "Pulse Live Kenya", "Nairobi Leo", "KBC"
    ],
    swahili: ["Tuko Kiswahili", "KBC Kiswahili", "Habari Now"],
    kikuyu: ["Coro FM", "Inooro FM", "Kameme FM"]
  };

  const currentSources = sources[lang];

  const categories = [
    { name: "GVB", img: CATEGORY_IMAGES.GVB },
    { name: "Crime", img: CATEGORY_IMAGES.Crime },
    { name: "Politics", img: CATEGORY_IMAGES.Politics },
    { name: "Business", img: CATEGORY_IMAGES.Business },
    { name: "Other", img: CATEGORY_IMAGES.Other },
  ];

  const exploreItems = [
    { name: "People", img: EXPLORE_IMAGES.People },
    { name: "Places", img: EXPLORE_IMAGES.Places },
    { name: "Organizations", img: EXPLORE_IMAGES.Organizations },
  ];

  return (
    <>
      {/* SECTION 1: Hero Categories */}
      <div className="grid grid-cols-1 md:grid-cols-5 h-screen">
        {categories.map((cat) => (
          <Link
            key={cat.name}
            to={`/category/${cat.name.toLowerCase()}`}
            className="relative overflow-hidden group cursor-pointer h-full"
          >
            <div
              className="absolute inset-0 bg-cover bg-center bg-no-repeat transform group-hover:scale-110 transition-transform duration-1000"
              style={{ backgroundImage: `url(${cat.img})` }}
            />
            <div className="absolute inset-0 bg-black/50 group-hover:bg-black/60 transition-all duration-500" />
            <div className="relative h-full flex flex-col items-center justify-center text-center px-6">
              <h2 className="text-5xl md:text-6xl lg:text-7xl font-black text-white tracking-wider drop-shadow-2xl
                             transform group-hover:scale-110 group-hover:-translate-y-6 transition-all duration-700">
                {cat.name}
              </h2>
              <p className="mt-4 text-lg md:text-xl text-white/90 font-medium opacity-0 group-hover:opacity-100 
                            transform translate-y-4 group-hover:translate-y-0 transition-all duration-700">
                Latest updates
              </p>
            </div>
          </Link>
        ))}
      </div>

      {/* SECTION 2: Trusted News Sources – Same Beautiful Look for ALL Languages */}
      <div className="bg-gray-50 dark:bg-gray-900 py-16 border-t border-gray-200 dark:border-gray-800">
        <div className="max-w-7xl mx-auto px-6">
          <h2 className="text-3xl md:text-4xl font-bold text-center mb-4 text-gray-800 dark:text-white">
            Trusted News Sources
          </h2>
          <p className="text-center text-sm text-gray-600 dark:text-gray-400 mb-10 max-w-2xl mx-auto">
            We monitor only high-quality, reputable Kenyan news outlets — every single day.
          </p>

          {/* Language Tabs */}
          <div className="flex justify-center gap-4 mb-12 flex-wrap">
            {(["english", "swahili", "kikuyu"] as const).map((l) => (
              <button
                key={l}
                onClick={() => setLang(l)}
                aria-pressed={lang === l}
                className={`px-8 py-3 rounded-full font-semibold transition-all border-2 ${
                  lang === l
                    ? "bg-blue-600 text-white border-blue-600 shadow-lg"
                    : "bg-white dark:bg-gray-800 border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 hover:border-blue-500"
                }`}
              >
                {l === "english" ? "English" : l === "swahili" ? "Kiswahili" : "Gĩkũyũ"} ({sources[l].length})
              </button>
            ))}
          </div>

          {/* Vertical Grid – Looks Perfect in ALL Languages */}
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-8">
            {currentSources.map((site) => (
              <Link
                key={site}
                to={`/category/${encodeURIComponent(site.toLowerCase())}`}
                className="group flex flex-col items-center justify-center bg-white dark:bg-gray-800 
                           rounded-2xl shadow-lg hover:shadow-2xl border border-gray-200 dark:border-gray-700 
                           p-8 transition-all duration-300 hover:scale-105"
              >
                <div className="w-32 h-32 mb-4 flex items-center justify-center bg-gray-100 dark:bg-gray-700 rounded-xl">
                  <img
                    src={NEWS_LOGOS[site] || `https://ui-avatars.com/api/?name=${encodeURIComponent(site)}&background=6366f1&color=fff&bold=true&size=256`}
                    alt={`${site} logo`}
                    className="max-w-full max-h-full object-contain p-3"
                    loading="lazy"
                    onError={(e) => {
                      // Fallback to avatar if logo fails
                      e.currentTarget.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(site)}&background=6366f1&color=fff&bold=true&size=256`;
                    }}
                  />
                </div>
                <p className="text-center font-medium text-gray-700 dark:text-gray-200 group-hover:text-blue-600 dark:group-hover:text-blue-400 transition-colors text-sm">
                  {site}
                </p>
              </Link>
            ))}
          </div>
        </div>
      </div>

      {/* SECTION 3: Explore Kenya */}
      <div className="py-20 bg-gradient-to-b from-gray-100 to-white dark:from-gray-900 dark:to-gray-800">
        <div className="max-w-7xl mx-auto px-6">
          <h2 className="text-4xl md:text-5xl font-extrabold text-center mb-16 text-gray-800 dark:text-white">
            News About Kenya Today
          </h2>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
            {exploreItems.map((item) => (
              <Link
                key={item.name}
                to={`/category/${item.name.toLowerCase()}`}
                className="relative h-96 rounded-3xl overflow-hidden group cursor-pointer shadow-2xl"
              >
                <div
                  className="absolute inset-0 bg-cover bg-center transform group-hover:scale-110 transition-transform duration-1000"
                  style={{ backgroundImage: `url(${item.img})` }}
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/30 to-transparent" />

                <div className="absolute bottom-0 left-0 right-0 p-10 text-white">
                  <h3 className="text-4xl md:text-5xl font-black mb-3 transform group-hover:-translate-y-3 transition-transform duration-500">
                    {item.name}
                  </h3>
                  <p className="text-lg opacity-90 group-hover:opacity-100 transform translate-y-4 group-hover:translate-y-0 transition-all duration-500">
                    Discover stories about {item.name.toLowerCase()} making headlines
                  </p>
                </div>

                <div className="absolute top-6 right-6 bg-white/20 backdrop-blur-md px-5 py-2 rounded-full">
                  <span className="text-white font-semibold">View All</span>
                </div>
              </Link>
            ))}
          </div>
        </div>
      </div>
    </>
  );
}